import React from 'react';
import styles from './Header.module.css';
import ThemeToggle from '../ThemeToggle/ThemeToggle'; // adjust path if needed

const Header = ({ navLinks, dark, toggleTheme }) => {
  const onDownloadCV = () => {
    const link = document.createElement('a');
    link.href = '/cv.pdf'; // path relative to public folder
    link.download = 'cv.pdf'; // file name for download
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <header className={styles.header}>
      <div className={styles.logo}>NP</div>
      <nav>
        <ul className={styles.navList}>
          {navLinks.map(link => (
            <li key={link.href}>
              <a href={link.href} className={styles.navLink}>
                {link.label}
              </a>
            </li>
          ))}
        </ul>
      </nav>
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
        <button
          type="button"
          className={styles.cvBtn}
          onClick={onDownloadCV}
        >
          <span style={{ fontSize: '1.5rem', verticalAlign: 'middle' }}>⬇️</span> Download CV
        </button>
        <ThemeToggle dark={dark} toggleTheme={toggleTheme} />
      </div>
    </header>
  );
};

export default Header;
