import React from 'react';
import styles from '../../App.module.css';

const Header = ({ navLinks, onDownloadCV, dark, toggleTheme }) => (
  <header className="fixed top-0 left-0 w-full z-50 bg-gradient-to-r from-[#1a1333] to-[#181028] bg-opacity-90 backdrop-blur-md shadow-lg flex items-center justify-between px-8 py-3">
    <div className="text-purple-400 font-bold text-2xl">Sb.</div>
    <nav>
      <ul className="flex gap-8 items-center font-medium text-white">
        {navLinks.map(link => (
          <li key={link.href}>
            <a href={link.href} className="hover:text-purple-400 flex items-center gap-1">
              <i className={`fas fa-${link.icon}`}></i> {link.label}
            </a>
          </li>
        ))}
      </ul>
    </nav>
    <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
      <button className="ml-4 px-3 py-1 rounded bg-purple-700 text-white hover:bg-purple-500 transition" onClick={onDownloadCV}>
        Download CV
      </button>
      <button
        className={styles.themeToggleBtn}
        onClick={toggleTheme}
        aria-label="Toggle dark mode"
        title={dark ? "Switch to Light Mode" : "Switch to Dark Mode"}
      >
        {dark ? (
          <span role="img" aria-label="Moon" style={{ fontSize: '1.3rem' }}>üåô</span>
        ) : (
          <span role="img" aria-label="Sun" style={{ fontSize: '1.3rem' }}>‚òÄÔ∏è</span>
        )}
      </button>
    </div>
  </header>
);

export default Header;
