import React from 'react';

const Header = ({ navLinks, onDownloadCV, dark, toggleTheme }) => (
  <header className="fixed top-4 left-1/2 transform -translate-x-1/2 w-[90vw] max-w-5xl z-50 bg-gradient-to-r from-[#1a1333]/90 to-[#181028]/90 backdrop-blur-md shadow-xl rounded-2xl px-8 py-3 flex items-center justify-between">
    <div className="text-purple-400 font-bold text-2xl">NP</div>
    <nav>
      <ul className="flex flex-row gap-8 items-center font-medium text-white m-0 p-0 list-none">
        {navLinks.map(link => (
          <li key={link.href}>
            <a href={link.href} className="hover:text-purple-400 transition-colors duration-150 flex items-center gap-1">
              {link.label}
            </a>
          </li>
        ))}
      </ul>
    </nav>
    <div className="flex items-center gap-4">
      <button
        type="button"
        className="px-4 py-2 rounded-lg bg-gradient-to-r from-purple-600 to-pink-500 text-white font-semibold shadow-md hover:from-pink-500 hover:to-purple-600 transition-all duration-200 flex items-center gap-2"
        onClick={onDownloadCV}
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v2a2 2 0 002 2h12a2 2 0 002-2v-2M7 10l5 5m0 0l5-5m-5 5V4" />
        </svg>
        Download CV
      </button>
      <button
        className={`ml-2 text-2xl rounded-full p-2 ${dark ? "bg-yellow-400/20 text-yellow-300" : "bg-purple-700/20 text-purple-400"} hover:bg-white/20 transition`}
        onClick={toggleTheme}
        aria-label="Toggle dark mode"
        title={dark ? "Switch to Light Mode" : "Switch to Dark Mode"}
      >
        {dark ? (
          <span role="img" aria-label="Moon">üåô</span>
        ) : (
          <span role="img" aria-label="Sun">‚òÄÔ∏è</span>
        )}
      </button>
    </div>
  </header>
);

export default Header;
